import React, { useState } from 'react';

let bgcolor = "orange";
let textcolors = "green";
let data = "Project 1 started..."

const App = () => {
  const [bgcolor, setBgColor] = useState("orange");
  const [text, setText] = useState("Project 1 started...");
  const [colors, setColors] = useState(textcolors)


  const changebgcolor = () => {
    setBgColor('cyan');
    setText("Project 1 completed...");
    setColors("red");
  }

  return (
    <>

      <div style={{ backgroundColor: bgcolor, color: colors }}>
        <h1 className="text-center">{data}</h1>
      </div>
      <div className="btn">
        <button className="btn btn-success" onClick={changebgcolor}>Click Me</button>
      </div >
    </>
  )
}


export default App
