import React, { useState } from 'react';

let bgcolor = "orange";
let text = "Project 1 started...";
let colors = "green";

const App = () => {
  const [bgcolor, setBgColor] = useState(bgcolor);
  const [text, setText] = useState(text);
  const [colors, setColors] = useState(colors)


  const changebgcolor = () => {
    setBgColor('cyan');
    setText("Project 1 completed...");
    setColors("red");
  }
  const normalData = () => {
    setBgColor(bgcolor);
    setText(text);
    setColors(colors);
  }



  return (
    <>

      <div style={{ backgroundColor: bgcolor, color: colors }}>
        <h1 className="text-center">{text}</h1>
      </div>
      <div className="btn">
        <button className="btn btn-success" onClick={changebgcolor} onDoubleClick={normalData}>Click Me</button>
      </div >
    </>
  )
}


export default App
